mapper.select:
SELECT t1.*,t2.avatar FROM t_purchase_inquiry_chat t1,t_user_list t2 WHERE t1.sender=t2.usercode and t1.purchasesn=#{purchasesn}
and t1.inquiry_id=#{inquiry_id}
and t1.inquiry_id is null
ORDER BY t1.createtime asc

		SELECT t1.*,t2.typename as sendtypename
		  FROM t_purchase_list t1 ,t_base_sendtype t2
		  
		    t1.sendtype = t2.id
              
                and t1.userCode=#{userCode} 
             
              
                 and t1.purchasesn like concat('%',#{purchasesn},'%')
             
              

                 and t1.createtime  >  #{timeBegin}
             
              

                  and t1.createtime  <  #{timeEnd}
             
              
                 and t1.stage = #{stage} 
                
              
                 and t1.status = #{status} 
                
              
                 and t1.sendtype = #{sendtype} 
                
		  
        
		 ORDER BY  
		 	CONCAT(t1.status,t1.createtime) DESC

mapper.delete:
  
        delete from t_purchase_goods where id in (
	         
	       		#{item.id}
	        
        )

mapper.update:
  
        UPDATE 
			t_purchase_list t
		SET 
			t.waybillfee=#{fee}
		WHERE
			t.purchasesn=#{purchasesn}

  
        UPDATE 
			t_purchase_goods t
		SET 
			t.realprice=#{price}
		WHERE
			t.id=#{id}

  
        UPDATE 
			t_purchase_inquiry t
		SET 
			t.flag=#{flag}
		WHERE
			t.id=#{id}

mapper.select:
		SELECT t1.purchasesn,t1.stage,t1.status,t1.sendtype,t1.address,t1.deliverytime,t1.currency,t2.typename as sendtypename
		  FROM t_purchase_list t1 ,t_base_sendtype t2
		  
		    t1.sendtype = t2.id  
		    and t1.purchasesn in  (select t3.purchasesn from t_purchase_inquiry t3 where t3.usercode = #{userCode})
              
                 and t1.purchasesn like concat('%',#{purchasesn},'%')
             
              

                 and t1.createtime  >  #{timeBegin}
             
              

                  and t1.createtime  <  #{timeEnd}
             
              
                 and t1.stage = #{stage} 
                
              
                 and t1.status = #{status} 
                
              
                 and t1.sendtype = #{sendtype} 
                
		  
        
		 ORDER BY  
		 	CONCAT(t1.status,t1.createtime) DESC
