mapper.select:
		SELECT id,goodsname,price,slt ,barcode,ifB2B,ifBBC,id as 'key'
		  FROM t_goods_list
		   
		    ifB2B='1' 
              
                and supplierCode=#{userCode} 
             
              
                and ifXG= #{ifXG}  
                
              
                and ifHW= #{ifHW}  
                
              
                and ifBS= #{ifBS}  
                
              
                and ifMY= #{ifMY}  
              
              
                and country= #{country}  
              
              
                and catelog3= #{catelog3}  
              
              
                and brand= #{brands}  
              
              
                and (goodsName like concat('%',#{search},'%')  
                	 or goodsNameE like concat('%',#{search},'%')      
                	 or brand like concat('%',#{search},'%')      
                	 or brandE like concat('%',#{search},'%')      
                	 or barcode like concat('%',#{search},'%')      
                	 or supplierName like concat('%',#{search},'%')  )
              
          
		 ORDER BY id asc

		SELECT id,goodsname,price,slt ,barcode,ifB2B,ifBBC,id as 'key'
		  FROM t_goods_list
		   
		    1=1 
              
                and supplierCode=#{userCode} 
             
              
                and ifXG= #{ifXG}  
                
              
                and ifHW= #{ifHW}  
                
              
                and ifBS= #{ifBS}  
                
              
                and ifMY= #{ifMY}  
              
              
                and country= #{country}  
              
              
                and catelog3= #{catelog3}  
              
              
                and brand= #{brands}  
              
              
                and (goodsName like concat('%',#{search},'%')  
                	 or goodsNameE like concat('%',#{search},'%')      
                	 or brand like concat('%',#{search},'%')      
                	 or brandE like concat('%',#{search},'%')      
                	 or barcode like concat('%',#{search},'%')       )
              
          
		 ORDER BY id asc

		SELECT id,goodsname,price,slt ,barcode,ifB2B,ifBBC,id as 'key'
		  FROM t_goods_list
		   
		    ifB2B='1' and ifBBC ='0' 
              
                and supplierCode=#{userCode} 
             
              
                and ifXG= #{ifXG}  
                
              
                and ifHW= #{ifHW}  
                
              
                and ifBS= #{ifBS}  
                
              
                and ifMY= #{ifMY}  
              
              
                and country= #{country}  
              
              
                and catelog3= #{catelog3}  
              
              
                and brand= #{brands}  
              
              
                and (goodsName like concat('%',#{search},'%')  
                	 or goodsNameE like concat('%',#{search},'%')      
                	 or brand like concat('%',#{search},'%')      
                	 or brandE like concat('%',#{search},'%')      
                	 or barcode like concat('%',#{search},'%')      
                	 or supplierName like concat('%',#{search},'%')  )
              
          
		 ORDER BY id asc
