mapper.select:
		select 0 as id,brand as value
		from t_goods_list g ,t_base_origin o
		  
		  catelog1 = #{catelog1} and g.country = o.originname and   g.ifB2B='1' 
              
                and catelog2= #{catelog2}  
              
		   
                and g.ifXG= #{ifXG}  
                
              
                and g.ifHW= #{ifHW}  
              
              
                and g.ifRB= #{ifRB}  
              
              
                and g.ifHG= #{ifHG}  
                  
              
                and g.ifBS= #{ifBS}  
                
              
                and g.ifMY= #{ifMY}  
              
              
                and o.id= #{country}  
              
		
              
                and (g.goodsName like concat('%',#{search},'%')  
                	 or g.goodsNameE like concat('%',#{search},'%')      
                	 or g.brand like concat('%',#{search},'%')      
                	 or g.brandE like concat('%',#{search},'%')      
                	 or g.barcode like concat('%',#{search},'%')       )
              
          
		GROUP BY brand order by count(*) desc

		select 0 as id,brand as value
		from t_goods_list g ,t_base_origin o
		  
		 g.country = o.originname and   g.ifB2B='1' 
		   
                and g.ifXG= #{ifXG}  
                
              
                and g.ifHW= #{ifHW}  
              
              
                and g.ifRB= #{ifRB}  
              
              
                and g.ifHG= #{ifHG}  
                  
              
                and g.ifBS= #{ifBS}  
                
              
                and g.ifMY= #{ifMY}  
              
              
                and o.id= #{country}  
              
		
              
                and (g.goodsName like concat('%',#{search},'%')  
                	 or g.goodsNameE like concat('%',#{search},'%')      
                	 or g.brand like concat('%',#{search},'%')      
                	 or g.brandE like concat('%',#{search},'%')      
                	 or g.barcode like concat('%',#{search},'%')       )
              
          
		GROUP BY brand order by count(*) desc  LIMIT 0,20

		SELECT g.id,g.goodsname,g.price,g.slt ,g.barcode,g.ifB2B,g.ifBBC,g.id as 'key',g.endPrice,g.beginPrice ,g.country 
		  FROM t_goods_list g,t_base_origin o
		   
		   g.country = o.originname and   g.ifB2B='1' 
              
                and g.supplierCode=#{userCode} 
             
              
                and g.ifXG= #{ifXG}  
                
              
                and g.ifHW= #{ifHW}  
              
              
                and g.ifRB= #{ifRB}  
              
              
                and g.ifHG= #{ifHG}  
                  
              
                and g.ifBS= #{ifBS}  
                
              
                and g.ifMY= #{ifMY}  
              
              
                and o.id= #{country}  
              
              
                and g.catelog1= #{catelog1}  
              
              
                and g.catelog2= #{catelog2}  
              
              
                and g.catelog3= #{catelog3}  
              
              
                and g.brand= #{brands}  
              
              
                and (g.goodsName like concat('%',#{search},'%')  
                	 or g.goodsNameE like concat('%',#{search},'%')      
                	 or g.brand like concat('%',#{search},'%')      
                	 or g.brandE like concat('%',#{search},'%')      
                	 or g.barcode like concat('%',#{search},'%')       )
              
          
		 ORDER BY g.id asc
		 LIMIT #{startPage} ,#{pageSize}

		SELECT count(*)
		 FROM t_goods_list g,t_base_origin o
		   
		   g.country = o.originname and   g.ifB2B='1' 
              
                and supplierCode=#{userCode} 
             
              
                and ifXG= #{ifXG}  
              
              
                and ifRB= #{ifRB}  
              
              
                and ifHG= #{ifHG}  
                
              
                and ifHW= #{ifHW}  
                
              
                and ifBS= #{ifBS}  
                
              
                and ifMY= #{ifMY}  
              
              
                and o.id= #{country}  
              
              
                and catelog1= #{catelog1}  
              
              
                and catelog2= #{catelog2}  
              
              
                and catelog3= #{catelog3}  
              
              
                and brand= #{brands}  
              
              
                and (goodsName like concat('%',#{search},'%')  
                	 or goodsNameE like concat('%',#{search},'%')      
                	 or brand like concat('%',#{search},'%')      
                	 or brandE like concat('%',#{search},'%')      
                	 or barcode like concat('%',#{search},'%')      )
              
          
		 ORDER BY g.id asc

		SELECT id,goodsname,price,slt ,barcode,ifB2B,ifBBC,id as 'key'
		  FROM t_goods_list
		   
		    ifB2B='1' and ifBBC ='0' 
              
                and supplierCode=#{userCode} 
             
              
                and ifXG= #{ifXG}  
                
              
                and ifRB= #{ifRB}  
              
              
                and ifHG= #{ifHG}  
              
              
                and ifHW= #{ifHW}  
                
              
                and ifBS= #{ifBS}  
                
              
                and ifMY= #{ifMY}  
              
              
                and country= #{country}  
              
              
                and catelog3= #{catelog3}  
              
              
                and brand= #{brands}  
              
              
                and (goodsName like concat('%',#{search},'%')  
                	 or goodsNameE like concat('%',#{search},'%')      
                	 or brand like concat('%',#{search},'%')      
                	 or brandE like concat('%',#{search},'%')      
                	 or barcode like concat('%',#{search},'%')       )
              
          
		 ORDER BY id asc
