mapper.select:
		SELECT id,goodsname,price,slt ,barcode,ifB2B,ifBBC,id as 'key'
		  FROM t_goods_list
		   
		    ifB2B='1' 
              
                and supplierCode=#{userCode} 
             
              
                and ifXG= #{ifXG}  
                
              
                and ifHW= #{ifHW}  
              
              
                and ifRB= #{ifRB}  
              
              
                and ifHG= #{ifHG}  
                  
              
                and ifBS= #{ifBS}  
                
              
                and ifMY= #{ifMY}  
              
              
                and country= #{country}  
              
              
                and catelog3= #{catelog3}  
              
              
                and brand= #{brands}  
              
              
                and (goodsName like concat('%',#{search},'%')  
                	 or goodsNameE like concat('%',#{search},'%')      
                	 or brand like concat('%',#{search},'%')      
                	 or brandE like concat('%',#{search},'%')      
                	 or barcode like concat('%',#{search},'%')       )
              
          
		 ORDER BY id asc
		 LIMIT #{startPage} ,#{pageSize}

		SELECT count(*)
		  FROM t_goods_list
		   
		    ifB2B='1' 
              
                and supplierCode=#{userCode} 
             
              
                and ifXG= #{ifXG}  
              
              
                and ifRB= #{ifRB}  
              
              
                and ifHG= #{ifHG}  
                
              
                and ifHW= #{ifHW}  
                
              
                and ifBS= #{ifBS}  
                
              
                and ifMY= #{ifMY}  
              
              
                and country= #{country}  
              
              
                and catelog3= #{catelog3}  
              
              
                and brand= #{brands}  
              
              
                and (goodsName like concat('%',#{search},'%')  
                	 or goodsNameE like concat('%',#{search},'%')      
                	 or brand like concat('%',#{search},'%')      
                	 or brandE like concat('%',#{search},'%')      
                	 or barcode like concat('%',#{search},'%')      )
              
          
		 ORDER BY id asc

		SELECT id,goodsname,price,slt ,barcode,ifB2B,ifBBC,id as 'key'
		  FROM t_goods_list
		   
		    ifB2B='1' and ifBBC ='0' 
              
                and supplierCode=#{userCode} 
             
              
                and ifXG= #{ifXG}  
                
              
                and ifRB= #{ifRB}  
              
              
                and ifHG= #{ifHG}  
              
              
                and ifHW= #{ifHW}  
                
              
                and ifBS= #{ifBS}  
                
              
                and ifMY= #{ifMY}  
              
              
                and country= #{country}  
              
              
                and catelog3= #{catelog3}  
              
              
                and brand= #{brands}  
              
              
                and (goodsName like concat('%',#{search},'%')  
                	 or goodsNameE like concat('%',#{search},'%')      
                	 or brand like concat('%',#{search},'%')      
                	 or brandE like concat('%',#{search},'%')      
                	 or barcode like concat('%',#{search},'%')       )
              
          
		 ORDER BY id asc
