mapper.update:
  
        UPDATE 
			t_user_role t
		SET 
			t.role_id=#{roleid}
		WHERE
			t.user_id=#{userid}

  
        UPDATE 
			t_user_list t
		SET 
			t.verifycode=#{verifycode},t.failmark=#{failmark}
		WHERE
			t.id=#{id}

  
        UPDATE 
			t_user_list t
		SET 
			t.flag=#{flag}
		WHERE
			t.id=#{id}

  
        UPDATE
			t_user_list t 
		SET 
			t.pwd=#{password}
		WHERE
			t.usercode=#{account}
